import React, { useState, useRef, useEffect } from "react";
import { sendToGemini } from "../services/geminiApi";
import { db, auth } from "../services/firebase";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";

const ChatPage = ({ persona }) => {
  const [userInput, setUserInput] = useState("");
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const formatPrompt = (input) => {
    if (persona === "Child") return `Answer playfully like talking to a child: ${input}`;
    if (persona === "Scholar") return `Explain deeply and thoughtfully: ${input}`;
    if (persona === "Adventurer") return `Respond with energy and daring spirit: ${input}`;
    return input;
  };

  async function handleSend() {
    if (!userInput.trim() || isLoading) return;

    setIsLoading(true);
    const formatted = formatPrompt(userInput);

    try {
      const reply = await sendToGemini(formatted);
      const newMessage = { user: userInput, sage: reply, timestamp: new Date() };
      setMessages([...messages, newMessage]);
      setUserInput("");

      await addDoc(collection(db, "chats"), {
        userId: auth.currentUser?.uid,
        persona,
        question: userInput,
        response: reply,
        createdAt: serverTimestamp()
      });
    } catch (error) {
      console.error("Error sending message:", error);
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <div className="flex flex-col h-[calc(100vh-4rem)] max-w-4xl mx-auto w-full p-4">
      <div className="flex items-center mb-6">
        <h2 className="text-2xl font-bold">Inspector Sage</h2>
        <span className="ml-2 px-3 py-1 bg-purple-600/20 text-purple-400 rounded-full text-sm">
          {persona}
        </span>
      </div>

      <div className="flex-1 overflow-y-auto mb-4 space-y-4">
        {messages.map((m, idx) => (
          <React.Fragment key={idx}>
            <div className={`p-4 max-w-[80%] ${idx % 2 === 0 ? 'message-user' : 'message-bot'}`}>
              <p className="font-medium">{idx % 2 === 0 ? 'You' : 'Sage'}</p>
              <p className="mt-1">{idx % 2 === 0 ? m.user : m.sage}</p>
            </div>
          </React.Fragment>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="sticky bottom-0 bg-gradient-to-t from-bg/80 to-transparent pt-4 pb-6">
        <div className="flex gap-2 items-center glass-card p-2 rounded-xl">
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask Inspector Sage..."
            className="flex-1 bg-transparent border-none focus:outline-none p-2"
            disabled={isLoading}
          />
          <button
            onClick={handleSend}
            disabled={isLoading || !userInput.trim()}
            className="p-2 rounded-lg bg-purple-600 hover:bg-purple-700 transition-colors disabled:opacity-50"
          >
            {isLoading ? (
              <div className="loader"></div>
            ) : (
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-8.707l-3-3a1 1 0 00-1.414 1.414L10.586 9H7a1 1 0 100 2h3.586l-1.293 1.293a1 1 0 101.414 1.414l3-3a1 1 0 000-1.414z" clipRule="evenodd" />
              </svg>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
