import React, { useState } from "react";
import { sendToGemini } from "../services/geminiApi";
import { db, auth } from "../services/firebase";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";

const ChatPage = ({ persona }) => {
  const [userInput, setUserInput] = useState("");
  const [messages, setMessages] = useState([]);

  const formatPrompt = (input) => {
    if (persona === "Child") return `Answer playfully like talking to a child: ${input}`;
    if (persona === "Scholar") return `Explain deeply and thoughtfully: ${input}`;
    if (persona === "Adventurer") return `Respond with energy and daring spirit: ${input}`;
    return input;
  };

  async function handleSend() {
    if (!userInput.trim()) return;

    const formatted = formatPrompt(userInput);
    const reply = await sendToGemini(formatted);

    const newMessage = { user: userInput, sage: reply, timestamp: new Date() };
    setMessages([...messages, newMessage]);
    setUserInput("");

    // Save chat metadata to Firestore
    await addDoc(collection(db, "chats"), {
      userId: auth.currentUser?.uid,
      persona,
      question: userInput,
      response: reply,
      createdAt: serverTimestamp()
    });
  }

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Inspector Sage ({persona})</h2>
      <div className="space-y-4 mb-4">
        {messages.map((m, idx) => (
          <div key={idx} className="border p-3 rounded">
            <p><strong>You:</strong> {m.user}</p>
            <p><strong>Sage:</strong> {m.sage}</p>
          </div>
        ))}
      </div>
      <div className="flex">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Ask Inspector Sage..."
          className="border p-2 flex-1"
        />
        <button onClick={handleSend} className="bg-blue-600 text-white px-4 py-2 ml-2 rounded">
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatPage;
