import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from "../services/firebase";

const personas = [
  { 
    name: "Child", 
    icon: "ðŸ§¸",
    description: "Playful and simple explanations"
  },
  { 
    name: "Scholar", 
    icon: "ðŸ“˜",
    description: "Detailed and thoughtful answers" 
  },
  { 
    name: "Adventurer", 
    icon: "ðŸ§­",
    description: "Energetic and daring responses"
  }
];

const PersonaSelect = ({ setPersona }) => {
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (!user) {
        navigate("/");
      } else if (!user.emailVerified) {
        navigate("/");
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  function handleSelect(persona) {
    setPersona(persona);
    navigate("/chat");
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-6">
      <h1 className="text-3xl font-bold mb-2 text-center">Choose Your Persona</h1>
      <p className="text-lg text-purple-400 mb-8 text-center">Select how Inspector Sage should respond to you</p>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-4xl">
        {personas.map((p) => (
          <div
            key={p.name}
            className="glass-card p-6 rounded-xl cursor-pointer hover:scale-[1.02] transition-transform duration-300 flex flex-col items-center"
            onClick={() => handleSelect(p.name)}
          >
            <span className="text-5xl mb-4">{p.icon}</span>
            <h3 className="text-xl font-semibold mb-2">{p.name}</h3>
            <p className="text-sm text-center text-gray-400">{p.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PersonaSelect;
