import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from "../services/firebase";

const personas = [
  { name: "Child", icon: "ðŸ§¸" },
  { name: "Scholar", icon: "ðŸ“˜" },
  { name: "Adventurer", icon: "ðŸ§­" }
];

const PersonaSelect = ({ setPersona }) => {
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (!user) {
        navigate("/");
      } else if (!user.emailVerified) {
        navigate("/");
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  function handleSelect(persona) {
    setPersona(persona);
    navigate("/chat");
  }

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <h1 className="text-3xl font-bold mb-6">Choose Your Persona</h1>
      <div className="flex space-x-8">
        {personas.map((p) => (
          <button
            key={p.name}
            className="text-5xl hover:scale-110 transition-transform"
            onClick={() => handleSelect(p.name)}
          >
            {p.icon} <div className="text-xl">{p.name}</div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default PersonaSelect;
